import React, { useEffect, useState } from "react";
import api from "../api/Api";
import Swal from "sweetalert2";

function Citas() {
  const [clientes, setClientes] = useState([]);
  const [servicios, setServicios] = useState([]);
  const [citas, setCitas] = useState([]);
  const [nuevaCita, setNuevaCita] = useState({
    clienteId: "",
    servicioId: "",
    fecha: "",
    hora: "",
  });

  useEffect(() => {
    cargarDatos();
  }, []);

  const cargarDatos = async () => {
    try {
      const [clientesRes, serviciosRes, citasRes] = await Promise.all([
        api.get("/api/clientes"),
        api.get("/api/servicios"),
        api.get("/api/citas"),
      ]);
      setClientes(clientesRes.data);
      setServicios(serviciosRes.data);
      setCitas(citasRes.data);
    } catch (error) {
      Swal.fire("Error", "No se pudieron cargar los datos.", "error");
    }
  };

  const handleChange = (e) => {
    setNuevaCita({ ...nuevaCita, [e.target.name]: e.target.value });
  };

  const registrarCita = async (e) => {
    e.preventDefault();
    try {
      await api.post("/api/citas/registrar", {
        cliente: { id: nuevaCita.clienteId },
        servicio: { id: nuevaCita.servicioId },
        fecha: nuevaCita.fecha,
        hora: nuevaCita.hora,
      });
      Swal.fire("Ã‰xito", "Cita registrada correctamente", "success");
      cargarDatos();
    } catch (error) {
      Swal.fire("Error", "No se pudo registrar la cita.", "error");
    }
  };

  return (
    <div className="container mt-4">
      <h2>GestiÃ³n de Citas</h2>

      <form onSubmit={registrarCita} className="mb-4">
        <div className="row">
          <div className="col-md-3">
            <label>Cliente</label>
            <select name="clienteId" value={nuevaCita.clienteId} onChange={handleChange} className="form-select">
              <option value="">Seleccione</option>
              {clientes.map((c) => (
                <option key={c.id} value={c.id}>
                  {c.nombreCompleto}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label>Servicio</label>
            <select name="servicioId" value={nuevaCita.servicioId} onChange={handleChange} className="form-select">
              <option value="">Seleccione</option>
              {servicios.map((s) => (
                <option key={s.id} value={s.id}>
                  {s.nombre}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-3">
            <label>Fecha</label>
            <input type="date" name="fecha" value={nuevaCita.fecha} onChange={handleChange} className="form-control" />
          </div>
          <div className="col-md-3">
            <label>Hora</label>
            <input type="time" name="hora" value={nuevaCita.hora} onChange={handleChange} className="form-control" />
          </div>
        </div>
        <button className="btn btn-primary mt-3" type="submit">Registrar Cita</button>
      </form>

      <h4>Lista de Citas</h4>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Servicio</th>
            <th>Fecha</th>
            <th>Hora</th>
            <th>Estado</th>
          </tr>
        </thead>
        <tbody>
          {citas.map((cita) => (
            <tr key={cita.id}>
              <td>{cita.cliente?.nombreCompleto}</td>
              <td>{cita.servicio?.nombre}</td>
              <td>{cita.fecha}</td>
              <td>{cita.hora}</td>
              <td>{cita.estado}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Citas;
