import React, { useState } from "react";
import api from "../api/Api";

const CrearCliente = () => {
  const [formData, setFormData] = useState({
    nit: "",
    nombreCompleto: "",
    email: "",
    telefono: "",
    password: "",
  });

  const [mensaje, setMensaje] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/api/clientes", formData);
      setMensaje(`✅ Cliente creado con éxito: ${res.data.nombreCompleto}`);
      setFormData({ nit: "", nombreCompleto: "", email: "", telefono: "", password: "" });
    } catch (error) {
      console.error(error);
      setMensaje("❌ Error al crear cliente. Verifica los datos.");
    }
  };

  return (
    <div className="container mt-4" style={{ maxWidth: "500px" }}>
      <h3>Registrar Nuevo Cliente</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <label>NIT:</label>
          <input type="text" className="form-control" name="nit" value={formData.nit} onChange={handleChange} required />
        </div>

        <div className="form-group mb-3">
          <label>Nombre Completo:</label>
          <input type="text" className="form-control" name="nombreCompleto" value={formData.nombreCompleto} onChange={handleChange} required />
        </div>

        <div className="form-group mb-3">
          <label>Email:</label>
          <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} required />
        </div>

        <div className="form-group mb-3">
          <label>Teléfono:</label>
          <input type="text" className="form-control" name="telefono" value={formData.telefono} onChange={handleChange} required />
        </div>

        <div className="form-group mb-3">
          <label>Contraseña:</label>
          <input type="password" className="form-control" name="password" value={formData.password} onChange={handleChange} required />
        </div>

        <button type="submit" className="btn btn-primary w-100">Registrar</button>
      </form>

      {mensaje && <div className="alert alert-info mt-3">{mensaje}</div>}
    </div>
  );
};

export default CrearCliente;
