import React, { useState, useEffect } from "react";
import api from "../api/Api";
import Swal from "sweetalert2";

const Reportes = () => {
  const [reporte, setReporte] = useState({});
  const [fechaInicio, setFechaInicio] = useState("");
  const [fechaFin, setFechaFin] = useState("");

  useEffect(() => {
    cargarReporteGeneral();
  }, []);

  const cargarReporteGeneral = async () => {
    try {
      const res = await api.get("/api/reportes/general");
      setReporte(res.data);
    } catch (error) {
      Swal.fire("Error", "No se pudo cargar el reporte general.", "error");
    }
  };

  const generarPorFechas = async () => {
    try {
      const res = await api.get("/api/reportes/por-fechas", {
        params: { inicio: fechaInicio, fin: fechaFin },
      });
      Swal.fire("Reporte generado", `${res.data.length} facturas encontradas`, "info");
    } catch {
      Swal.fire("Error", "No se pudo generar el reporte por fechas", "error");
    }
  };

  return (
    <div className="container mt-4">
      <h2>ðŸ“Š Reporte General</h2>
      <div className="card p-3 mt-3 shadow-sm">
        <ul>
          <li>Total clientes: {reporte.totalClientes}</li>
          <li>Total servicios: {reporte.totalServicios}</li>
          <li>Total citas: {reporte.totalCitas}</li>
          <li>Total facturas: {reporte.totalFacturas}</li>
          <li>Total ingresos: Q{reporte.totalIngresos?.toFixed(2)}</li>
        </ul>
      </div>

      <hr />
      <h4 className="mt-4">ðŸ“… Reporte por fechas</h4>
      <div className="d-flex gap-2">
        <input
          type="date"
          className="form-control"
          value={fechaInicio}
          onChange={(e) => setFechaInicio(e.target.value)}
        />
        <input
          type="date"
          className="form-control"
          value={fechaFin}
          onChange={(e) => setFechaFin(e.target.value)}
        />
        <button className="btn btn-primary" onClick={generarPorFechas}>
          Generar
        </button>
      </div>
    </div>
  );
};

export default Reportes;
