import React, { useEffect, useState } from "react";
import axios from "axios";

const Facturas = () => {
  const [facturas, setFacturas] = useState([]);
  const [filtroCliente, setFiltroCliente] = useState("");
  const [fechaInicio, setFechaInicio] = useState("");
  const [fechaFin, setFechaFin] = useState("");
  const [loading, setLoading] = useState(false);

  // Cargar todas las facturas
  const cargarFacturas = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:8081/api/facturas");
      setFacturas(response.data);
    } catch (error) {
      console.error("‚ùå Error al cargar facturas:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    cargarFacturas();
  }, []);

  // Buscar por cliente
  const buscarPorCliente = async () => {
    if (!filtroCliente) return cargarFacturas();
    try {
      const response = await axios.get(
        `http://localhost:8081/api/facturas/cliente/${filtroCliente}`
      );
      setFacturas(response.data);
    } catch (error) {
      console.error("‚ùå Error al buscar facturas por cliente:", error);
    }
  };

  // Generar reporte entre fechas
  const generarReporte = async () => {
    if (!fechaInicio || !fechaFin) return alert("Selecciona ambas fechas.");
    try {
      const response = await axios.get(
        `http://localhost:8081/api/facturas/reporte?inicio=${fechaInicio}&fin=${fechaFin}`
      );
      setFacturas(response.data);
    } catch (error) {
      console.error("‚ùå Error al generar reporte:", error);
    }
  };

  const generarFactura = async () => {
  try {
    const response = await axios.post("http://localhost:8081/api/facturas/registrar", {
      idCliente: selectedClienteId,
      idsCitas: selectedCitasIds,
    });
    alert("‚úÖ Factura generada correctamente");
    console.log(response.data);
  } catch (error) {
    if (error.response && error.response.data.message) {
      alert("‚ö†Ô∏è " + error.response.data.message);
    } else {
      alert("‚ùå Error generando factura");
    }
  }
};

  // Descargar PDF
  const descargarPDF = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8081/api/reportes/descargar`,
        {
          responseType: "blob", // Recibir archivo binario
        }
      );

      const blob = new Blob([response.data], { type: "application/pdf" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = "reporte_facturas.pdf";
      link.click();
    } catch (error) {
      console.error("‚ùå Error al descargar PDF:", error);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">üìÑ Gesti√≥n de Facturas</h1>

      {/* Filtros */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="ID Cliente"
          value={filtroCliente}
          onChange={(e) => setFiltroCliente(e.target.value)}
          className="border p-2 rounded"
        />
        <button onClick={buscarPorCliente} className="bg-blue-600 text-white p-2 rounded">
          Buscar Cliente
        </button>

        <input
          type="date"
          value={fechaInicio}
          onChange={(e) => setFechaInicio(e.target.value)}
          className="border p-2 rounded"
        />
        <input
          type="date"
          value={fechaFin}
          onChange={(e) => setFechaFin(e.target.value)}
          className="border p-2 rounded"
        />
        <button onClick={generarReporte} className="bg-green-600 text-white p-2 rounded">
          Filtrar por Fechas
        </button>

        <button onClick={descargarPDF} className="bg-red-600 text-white p-2 rounded">
          Descargar PDF
        </button>
      </div>

      {/* Tabla de facturas */}
      {loading ? (
        <p>Cargando facturas...</p>
      ) : (
        <table className="table-auto w-full border">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">ID</th>
              <th className="p-2 border">Cliente</th>
              <th className="p-2 border">Fecha Emisi√≥n</th>
              <th className="p-2 border">Total</th>
              <th className="p-2 border">Citas Asociadas</th>
            </tr>
          </thead>
          <tbody>
            {facturas.map((f) => (
              <tr key={f.id}>
                <td className="p-2 border">{f.id}</td>
                <td className="p-2 border">{f.cliente?.nombreCompleto}</td>
                <td className="p-2 border">{f.fechaEmision}</td>
                <td className="p-2 border">{f.total.toFixed(2)}</td>
                <td className="p-2 border">{f.citas?.length || 0}</td>
                <td>{f.citas.some(c => c.estado === "COBRADA") ? "Cobrada" : "Pendiente"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Facturas;
